mapper.select:
SELECT id, product_name, quantity, price, sale_date
FROM sales
WHERE id = #{id}

mapper.update:
UPDATE sales
SET product_name = #{productName},
quantity = #{quantity},
price = #{price}
WHERE id = #{id}

update.foreach:
UPDATE sales
SET product_name = #{sale.productName},
quantity = #{sale.quantity},
price = #{sale.price}
WHERE id = #{sale.id}
